#![feature(unboxed_closures, fn_traits)]
//定义闭包结构体
struct Say { //捕获的环境变量 s: String,
} //实现FnOnce方法
impl FnOnce<()> for Say { type Output = (); extern "rust-call" fn call_once(self, args: ()) -> () { println!("{}", self.s); }
} //实现FnMut方法
impl FnMut<()> for Say { extern "rust-call" fn call_mut(&mut self, args: ()) -> () { self.s.push_str(" world!"); println!("{}", self.s); }
} //实现Fn方法
impl Fn<()> for Say { extern "rust-call" fn call(&self, args: ()) -> () { println!("{}", self.s); }
} fn main() { let mut greeting = "hello".to_string(); //闭包say捕获环境变量greeting let mut say = Say { s: greeting}; //不修改环境变量 不捕获所有权 say.call(()); //修改环境变量, 不捕获所有权 say.call_mut(()); //移除所有权 say.call_once(()); //之后再调用call call_mut call_once就会报错
}

