} 以下是rust实现 pub fn fx_directory_name_extract(source: &String, dest:&mut String) -> Option<String> { let mut idx: usize = 0; let mut j: usize; // /* Is a backslash present? */ if( (source.as_bytes()[0] == b'\\') || (source.as_bytes()[0] == b'/') ){ /* Advance the string pointer. */ idx += 1; } /* Loop to remove any leading spaces. */ while( source.as_bytes()[idx] == b' '){ /* Position past leading space. */ idx+= 1; } j = 0; for (i, c) in source.chars().enumerate() { j+=1; if i < idx { continue; } if( (c == '\\') || (c == '/')){ j-=1; break; } /* Long name can be at most 255 characters, but are further limited by the FX_MAX_LONG_NAME_LEN define. */ if (i == 256 - 1){ break; } dest.push(c); } /* Determine if the source string is now at the end. */ if ( j == source.len()) { /* Yes, return a NULL pointer. */ return None; } return Some( source[j..].to_string());
